package sec02.exam04_long;

public class LongExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		long var1 = 10;		// int		4바이트	숫자만 기입하면 기본형인 int 형으로 인식한다.
		long var2 = 20L;		// long	8바이트	숫자 뒤에 L을 붙여야지 Long으로 인식한다.
//		long var3 = 1000000000000; // The literal 1000000000000 of type int is out of range 
		long var4 = 1000000000000L;
		
//		float var5 = 3.14;
		float var6 = 3.14F;
		
		double var7 = 3.14; // 리터럴 실수형의 기본타입이다.
	}

}
